// src/components/ImageSlider.jsx
import Slider from "react-slick";
import { Box } from "@mui/material";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function ImageSlider() {
  const settings = {
    dots: true,              // show 3 dots
    infinite: true,
    speed: 500,              // faster animation
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 1500,     // fast sliding (1.5 seconds)
    arrows: false,           // cleaner UI
    pauseOnHover: true,
  };

  const images = [
    "https://images.unsplash.com/photo-1706353399656-210cca727a33?q=80&w=1170&auto=format&fit=crop",
    "https://images.unsplash.com/photo-1649751361457-01d3a696c7e6?w=600&auto=format&fit=crop&q=60",
    "https://images.unsplash.com/photo-1554284126-aa88f22d8b47?q=80&w=1170&auto=format&fit=crop"
  ];

  return (
    <Box sx={{ width: "100%", overflow: "hidden" }}>
      <Slider {...settings}>
        {images.map((src, index) => (
          <Box
            key={index}
            component="img"
            src={src}
            alt={`slide-${index}`}
            sx={{
              width: "100%",
              height: { xs: "250px", md: "450px" },
              objectFit: "cover",
            }}
          />
        ))}
      </Slider>
    </Box>
  );
}
