import React from "react";
import {
  Box,
  Container,
  Grid,
  Typography,
  Card,
  CardContent,
  Avatar,
} from "@mui/material";
import { motion } from "framer-motion";

/* ---------- Animations ---------- */
const container = {
  hidden: {},
  visible: {
    transition: { staggerChildren: 0.15 },
  },
};

const fadeUp = {
  hidden: { opacity: 0, y: 40 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.7, ease: "easeOut" },
  },
};

const imageHover = {
  hover: { scale: 1.05 },
};

const cardHover = {
  hover: {
    y: -10,
    boxShadow: "0px 20px 40px rgba(0,0,0,0.15)",
  },
};

/* ---------- Data ---------- */
const treatments = [
  {
    title: "Orthopedic Physiotherapy",
    desc: "Treatment for bones, joints, ligaments and muscles using evidence-based rehabilitation.",
    img: "https://aica.com/wp-content/uploads/2024/09/Top-5-Treatments-You-May-Receive-in-Orthopedic-Physiotherapy-e1612576187237.jpg",
  },
  {
    title: "Sports Physiotherapy",
    desc: "Injury prevention, performance enhancement and faster recovery for athletes.",
    img: "https://images.unsplash.com/photo-1571019613576-2b22c76fd955",
  },
  {
    title: "Neurological Rehabilitation",
    desc: "Focused therapy for stroke, Parkinson’s, spinal injuries and balance disorders.",
    img: "https://images.unsplash.com/photo-1579154204601-01588f351e67",
  },
  {
    title: "Geriatric Physiotherapy",
    desc: "Improving mobility, balance and strength for elderly patients.",
    img: "https://assets.thehansindia.com/h-upload/2023/04/05/1345227-pgyh.webp",
  },
  {
    title: "Pediatric Physiotherapy",
    desc: "Therapy for children with developmental and neurological conditions.",
    img: "https://images.unsplash.com/photo-1550831107-1553da8c8464",
  },
  {
    title: "Cardio-Pulmonary Physiotherapy",
    desc: "Breathing exercises and rehabilitation for heart and lung conditions.",
    img: "https://images.unsplash.com/photo-1584467735871-bf4ec5eae4e8",
  },
];

const doctors = [
  {
    name: "Dr. Ananya Rao",
    qualification: "MPT (Orthopedics), PhD",
    img: "https://randomuser.me/api/portraits/women/44.jpg",
  },
  {
    name: "Dr. Rajesh Kumar",
    qualification: "MPT (Sports Physiotherapy)",
    img: "https://randomuser.me/api/portraits/men/32.jpg",
  },
  {
    name: "Dr. Priya Sharma",
    qualification: "MPT (Neurology)",
    img: "https://randomuser.me/api/portraits/women/68.jpg",
  },
  {
    name: "Dr. Arjun Patel",
    qualification: "MPT (Cardio Pulmonary)",
    img: "https://randomuser.me/api/portraits/men/75.jpg",
  },
];

/* ---------- Component ---------- */
export default function About() {
  return (
    <Box sx={{ background: "#f5f9fc", py: 10 }}>
      <Container maxWidth="lg">

        {/* INTRO */}
        <motion.div variants={container} initial="hidden" whileInView="visible">
          <motion.div variants={fadeUp}>
            <Typography variant="h3" textAlign="center" fontWeight="bold">
              About PhysioTattva
            </Typography>
            <Typography textAlign="center" color="text.secondary" sx={{ mt: 2, mb: 8 }}>
              Healing through science, care, and movement.
            </Typography>
          </motion.div>
        </motion.div>

        {/* WHO WE ARE */}
        <Grid container spacing={6} alignItems="center">
          <Grid item xs={12} md={6}>
            <motion.img
              src=""
              alt="Clinic"
              style={{ width: "100%", borderRadius: 20 }}
              variants={fadeUp}
              initial="hidden"
              whileInView="visible"
              whileHover="hover"
              animate="visible"
              variants={imageHover}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <motion.div variants={fadeUp} initial="hidden" whileInView="visible">
              <Typography variant="h4" fontWeight="bold" gutterBottom>
                Who We Are
              </Typography>
              <Typography sx={{ lineHeight: 1.9 }}>
                PhysioTattva is a multidisciplinary physiotherapy clinic focused on
                personalized care. We believe movement is medicine and design customized
                rehabilitation programs to restore mobility and confidence.
              </Typography>
            </motion.div>
          </Grid>
        </Grid>

        {/* TREATMENTS */}
        <Typography variant="h4" textAlign="center" fontWeight="bold" sx={{ mt: 10 }}>
          Our Physiotherapy Treatments
        </Typography>

        <Grid container spacing={4} sx={{ mt: 4 }}>
          {treatments.map((t, i) => (
            <Grid item xs={12} md={4} key={i}>
              <motion.div
                variants={fadeUp}
                initial="hidden"
                whileInView="visible"
                whileHover="hover"
                animate="visible"
              >
                <motion.div variants={cardHover}>
                  <Card sx={{ height: "100%", borderRadius: 4 }}>
                    <motion.img
                      src={t.img}
                      alt={t.title}
                      style={{ width: "100%", height: 200, objectFit: "cover" }}
                      whileHover={{ scale: 1.08 }}
                    />
                    <CardContent>
                      <Typography variant="h6" fontWeight="bold">
                        {t.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {t.desc}
                      </Typography>
                    </CardContent>
                  </Card>
                </motion.div>
              </motion.div>
            </Grid>
          ))}
        </Grid>

        {/* DOCTORS */}
        <Typography variant="h4" textAlign="center" fontWeight="bold" sx={{ mt: 10 }}>
          Our Expert Doctors
        </Typography>

        <Grid container spacing={4} justifyContent="center" sx={{ mt: 4 }}>
          {doctors.map((doc, i) => (
            <Grid item xs={12} md={3} key={i}>
              <motion.div
                variants={fadeUp}
                initial="hidden"
                whileInView="visible"
                whileHover={{ scale: 1.05 }}
              >
                <Card sx={{ textAlign: "center", p: 4, borderRadius: 4 }}>
                  <Avatar
                    src={doc.img}
                    sx={{ width: 120, height: 120, mx: "auto", mb: 2 }}
                  />
                  <Typography fontWeight="bold">{doc.name}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    {doc.qualification}
                  </Typography>
                </Card>
              </motion.div>
            </Grid>
          ))}
        </Grid>

        {/* MISSION & VISION */}
        <Grid container spacing={6} sx={{ mt: 10 }}>
          {["Our Mission", "Our Vision"].map((title, i) => (
            <Grid item xs={12} md={6} key={i}>
              <motion.div variants={fadeUp} initial="hidden" whileInView="visible">
                <Typography variant="h4" fontWeight="bold">
                  {title}
                </Typography>
                <Typography sx={{ mt: 2, lineHeight: 1.9 }}>
                  {title === "Our Mission"
                    ? "To deliver compassionate, innovative and result-driven physiotherapy care."
                    : "To become the most trusted physiotherapy brand through excellence and innovation."}
                </Typography>
              </motion.div>
            </Grid>
          ))}
        </Grid>

      </Container>
    </Box>
  );
}
