// src/components/ImageSlider.jsx
import Slider from "react-slick";
import { Box } from "@mui/material";

export default function ImageSlider() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  const images = [
    // Replace these with your downloaded URLs if you save them locally
    "https://images.unsplash.com/photo-1706353399656-210cca727a33?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", // knee exercise
    "https://images.unsplash.com/photo-1649751361457-01d3a696c7e6?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxzZWFyY2h8N3x8cGh5c2lvdGhlcmFweXxlbnwwfHwwfHx8MA%3D%3D", // physio stretch
    "https://images.pexels.com/photos/4140297/pexels-photo-4140297.jpeg", // therapist assisting
  ];

  return (
    <Box sx={{ maxWidth: "100%", overflow: "hidden" }}>
      <Slider {...settings}>
        {images.map((src, i) => (
          <Box
            key={i}
            component="img"
            src={src}
            alt={`slide-${i}`}
            sx={{
              width: "100%",
              height: { xs: "250px", md: "450px" },
              objectFit: "cover",
            }}
          />
        ))}
      </Slider>
    </Box>
  );
}
