import { Grid, Card, CardContent, Typography, Box } from "@mui/material";
import { motion } from "framer-motion";
import FitnessCenterIcon from "@mui/icons-material/FitnessCenter";
import HealingIcon from "@mui/icons-material/Healing";
import AccessibilityNewIcon from "@mui/icons-material/AccessibilityNew";
import HomeIcon from "@mui/icons-material/Home";
import ElderlyIcon from "@mui/icons-material/Elderly";
import DirectionsWalkIcon from "@mui/icons-material/DirectionsWalk";

const services = [
  {
    title: "Sports Physiotherapy",
    desc: "Treatment and rehabilitation for sports injuries and muscle recovery.",
    icon: <FitnessCenterIcon fontSize="large" color="primary" />,
  },
  {
    title: "Chiropractic Care",
    desc: "Spine and posture correction with expert hands-on therapy.",
    icon: <HealingIcon fontSize="large" color="primary" />,
  },
  {
    title: "Neuro Rehabilitation",
    desc: "Advanced therapy for neurological disorders and mobility improvement.",
    icon: <AccessibilityNewIcon fontSize="large" color="primary" />,
  },
  {
    title: "Home Physiotherapy",
    desc: "Professional physiotherapy treatment at the comfort of your home.",
    icon: <HomeIcon fontSize="large" color="primary" />,
  },
  {
    title: "Geriatric Care",
    desc: "Special care programs for elderly patients to improve balance and strength.",
    icon: <ElderlyIcon fontSize="large" color="primary" />,
  },
  {
    title: "Vestibular Therapy",
    desc: "Treatment for dizziness, balance disorders and inner ear problems.",
    icon: <DirectionsWalkIcon fontSize="large" color="primary" />,
  },
];

export default function Services() {
  return (
    <Box sx={{ bgcolor: "#f5f7fa", py: 6 }}>
      <Typography variant="h4" textAlign="center" fontWeight="bold" gutterBottom>
        Our Services
      </Typography>

      <Grid container spacing={4} sx={{ px: 4, mt: 2 }}>
        {services.map((service, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.5 }}
            >
              <Card
                sx={{
                  height: "100%",
                  textAlign: "center",
                  borderRadius: 3,
                  boxShadow: 3,
                  p: 2,
                }}
              >
                <CardContent>
                  {service.icon}
                  <Typography variant="h6" fontWeight="bold" sx={{ mt: 2 }}>
                    {service.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                    {service.desc}
                  </Typography>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
