// src/components/About.jsx
import { Container, Typography, Grid, Box } from "@mui/material";
import { motion } from "framer-motion";

const sections = [
  {
    title: "Who We Are",
    desc: `PhysioTattva is a leading physiotherapy and rehabilitation centre committed to restoring movement and enhancing quality of life.

We deliver evidence-based treatments supported by advanced technology and modern rehabilitation techniques.

Our focus is on personalised care, accurate diagnosis, and effective therapy to help patients recover safely and regain confidence in movement.`,
    image:
      "https://images.unsplash.com/photo-1550831107-1553da8c8464?auto=format&fit=crop&w=900&q=80",
  },
  {
    title: "Our Clinics & Treatment",
    desc: `PhysioTattva clinics are designed with state-of-the-art infrastructure and modern rehabilitation equipment to support effective care.

We focus on personalized treatment plans tailored to individual needs, ensuring faster, safer recovery while improving mobility, strength, and overall wellbeing.`,
    image:
      "https://images.unsplash.com/photo-1579154204601-01588f351e67?auto=format&fit=crop&w=900&q=80",
  },
];

export default function About() {
  return (
    <Box sx={{ bgcolor: "#f5f7fa", py: 8 }}>
      <Container maxWidth="lg">

        {/* Heading */}
        <motion.div
          initial={{ opacity: 0, y: -30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Typography variant="h3" textAlign="center" fontWeight="bold" gutterBottom>
            About PhysioTattva
          </Typography>

          <Typography
            variant="h6"
            textAlign="center"
            color="text.secondary"
            sx={{ mb: 6 }}
          >
            PhysioTattva is a holistic physiotherapy and rehabilitation centre dedicated to healing
            through science, care, and movement. We combine evidence-based clinical practices with
            compassionate, patient-centred treatment to restore strength, mobility, and confidence.
          </Typography>
        </motion.div>

        {/* Sections */}
        {sections.map((item, index) => (
          <Grid
            container
            spacing={5}
            alignItems="center"
            sx={{ mb: 8 }}
            key={index}
            direction={index % 2 === 0 ? "row" : "row-reverse"}
          >
            {/* IMAGE */}
            <Grid item xs={12} md={6}>
              <motion.img
                src={item.image}
                alt={item.title}
                style={{
                  width: "100%",
                  height: "400px",
                  objectFit: "cover",
                  borderRadius: "16px",
                }}
                initial={{ opacity: 0, x: index % 2 === 0 ? -60 : 60 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.8 }}
              />
            </Grid>

            {/* CONTENT */}
            <Grid item xs={12} md={6}>
              <motion.div
                initial={{ opacity: 0, y: 40 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8 }}
              >
                <Typography variant="h4" fontWeight="bold" gutterBottom>
                  {item.title}
                </Typography>

                <Typography
                  sx={{
                    fontSize: "1.1rem",
                    lineHeight: 1.8,
                    whiteSpace: "pre-line",
                  }}
                >
                  {item.desc}
                </Typography>
              </motion.div>
            </Grid>
          </Grid>
        ))}

      </Container>
    </Box>
  );
}
