// src/components/About.jsx
import { Container, Typography, Grid, Box } from "@mui/material";
import { motion } from "framer-motion";

const sections = [
  {
    title: "Who We Are",
    desc: "PhysioTattva is a leading physiotherapy and rehabilitation center providing evidence-based treatments with advanced technology to restore movement and improve quality of life.",
    images: [
      "https://images.unsplash.com/photo-1550831107-1553da8c8464?auto=format&fit=crop&w=900&q=80",
      "https://images.unsplash.com/photo-1550831107-1553da8c8464?auto=format&fit=crop&w=900&q=80",
    ],
  },
  {
    title: "Our Clinics & Treatment",
    desc: "State-of-the-art clinics with modern equipment and personalized treatment plans for faster and safer recovery.",
    images: [
      "https://images.unsplash.com/photo-1579154204601-01588f351e67?auto=format&fit=crop&w=900&q=80",
      "https://images.unsplash.com/photo-1579154204601-01588f351e67?auto=format&fit=crop&w=900&q=80",
    ],
  },
];

export default function About() {
  return (
    <Box sx={{ bgcolor: "#f5f7fa", py: 8 }}>
      <Container>

        {/* Heading */}
        <motion.div
          initial={{ opacity: 0, y: -30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Typography variant="h3" textAlign="center" fontWeight="bold" gutterBottom>
            About PhysioTattva
          </Typography>
          <Typography variant="h6" textAlign="center" color="text.secondary" sx={{ mb: 6 }}>
            Healing through science, care and movement
          </Typography>
        </motion.div>

        {/* Sections */}
        {sections.map((item, index) => (
          <Grid
            container
            spacing={4}
            alignItems="center"
            sx={{ mb: 6 }}
            key={index}
            direction={index % 2 === 0 ? "row" : "row-reverse"}
          >
            {/* Images (2 per section) */}
            <Grid item xs={12} md={6}>
              <Grid item xs={12} md={6}>
  <Grid container spacing={2}>
    {item.images.map((img, i) => (
      <Grid item xs={12} key={i}>
        <motion.img
          src={img}
          alt={`about-${i}`}
          style={{
            width: "600px",        // full width
            height: "400px",      // fixed height
            objectFit: "cover",
            borderRadius: "12px",
          }}
          initial={{ opacity: 0, scale: 0.9 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.6 }}
        />
      </Grid>
    ))}
  </Grid>
</Grid>

            </Grid>

            {/* Text */}
            <Grid item xs={12} md={6}>
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8 }}
              >
                <Typography variant="h4" fontWeight="bold" gutterBottom>
                  {item.title}
                </Typography>
                <Typography variant="body1" sx={{ fontSize: "1.1rem" }}>
                  {item.desc}
                </Typography>
              </motion.div>
            </Grid>
          </Grid>
        ))}

      </Container>
    </Box>
  );
}
