// src/components/Testimonials.jsx
import { Container, Typography, Box } from "@mui/material";
import { motion } from "framer-motion";

const testData = [
  "Great recovery and professional care!",
  "Highly focused and effective therapists.",
  "Back pain relief that truly works."
];

export default function Testimonials() {
  return (
    <Container sx={{ py: 8 }}>
      <Typography variant="h4" gutterBottom>Testimonials</Typography>
      <Box>
        {testData.map((text, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ delay: index * 0.2 }}
          >
            <Typography sx={{ mb: 2, fontStyle: "italic" }}>"{text}"</Typography>
          </motion.div>
        ))}
      </Box>
    </Container>
  );
}
