import React from "react";
import { Box, Container, Typography, Button, Grid, useTheme } from "@mui/material";
import { motion } from "framer-motion";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";

const containerVariants = {
  hidden: {},
  visible: { transition: { staggerChildren: 0.2 } },
};

const fadeUp = {
  hidden: { opacity: 0, y: 30 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } },
};

export default function Hero() {
  const theme = useTheme();

  return (
    <Box
      sx={{
        minHeight: "90vh",
        display: "flex",
        alignItems: "center",
        position: "relative",
        overflow: "hidden",
        background: `linear-gradient(135deg, ${theme.palette.background.default}, rgba(0,150,136,0.08))`,
      }}
    >
      {/* Floating gradient blob */}
      <motion.div
        animate={{ y: [0, -20, 0] }}
        transition={{ duration: 6, repeat: Infinity }}
        style={{
          position: "absolute",
          top: "-15%",
          right: "-10%",
          width: 500,
          height: 500,
          borderRadius: "50%",
          background: "radial-gradient(circle, rgba(0,150,136,0.15), transparent 70%)",
          filter: "blur(40px)",
        }}
      />

      <Container maxWidth="xl" sx={{ position: "relative", zIndex: 2 }}>
        <Grid container spacing={6} alignItems="center">

          {/* LEFT CONTENT */}
          <Grid item xs={12} md={6}>
            <motion.div variants={containerVariants} initial="hidden" animate="visible">

              <motion.div variants={fadeUp}>
                <Typography
                  variant="overline"
                  sx={{
                    bgcolor: "rgba(0,150,136,0.1)",
                    px: 2,
                    py: 0.5,
                    borderRadius: 10,
                    display: "inline-block",
                    mb: 2,
                    color: "primary.main",
                  }}
                >
                  Welcome to PhysioTattva
                </Typography>
              </motion.div>

              <motion.div variants={fadeUp}>
                <Typography variant="h2" fontWeight="bold">
                  Reclaim Your{" "}
                  <Box component="span" sx={{ color: "primary.main" }}>
                    Movement
                  </Box>{" "}
                  & Life
                </Typography>
              </motion.div>

              <motion.div variants={fadeUp}>
                <Typography variant="h6" color="text.secondary" sx={{ mt: 2, mb: 4, maxWidth: 500 }}>
                  World-class physiotherapy and rehabilitation care tailored to your needs with advanced technology.
                </Typography>
              </motion.div>

              <motion.div variants={fadeUp}>
                <Box sx={{ display: "flex", gap: 2, flexWrap: "wrap" }}>
                  <Button
                    variant="contained"
                    size="large"
                    endIcon={<ArrowForwardIcon />}
                    sx={{
                      px: 4,
                      py: 1.5,
                      borderRadius: "30px",
                      background: "linear-gradient(135deg,#009688,#26c6da)",
                      boxShadow: "0 10px 30px rgba(0,150,136,0.4)",
                    }}
                  >
                    Book Appointment
                  </Button>

                  <Button variant="outlined" size="large" sx={{ px: 4, py: 1.5, borderRadius: "30px" }}>
                    View Services
                  </Button>
                </Box>
              </motion.div>

              {/* STATS */}
              <motion.div variants={fadeUp}>
                <Box sx={{ mt: 6, display: "flex", gap: 4 }}>
                  {[
                    { num: "10k+", label: "Happy Patients" },
                    { num: "15+", label: "Years Experience" },
                    { num: "50+", label: "Expert Doctors" },
                  ].map((item, i) => (
                    <Box key={i}>
                      <Typography variant="h4" fontWeight="bold">{item.num}</Typography>
                      <Typography variant="body2" color="text.secondary">{item.label}</Typography>
                    </Box>
                  ))}
                </Box>
              </motion.div>

            </motion.div>
          </Grid>

          {/* RIGHT IMAGE */}
          <Grid item xs={12} md={6}>
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1 }}
              style={{ display: "flex", justifyContent: "center" }}
            >
              {/* Fixed shape container */}
              <Box
                sx={{
                  width: "80%",
                  height: "450px",
                  borderRadius: "24px",
                  overflow: "hidden",
                  boxShadow: "0 20px 50px rgba(0,0,0,0.15)",
                }}
              >
                <Box
                  component="img"
                  src="https://images.unsplash.com/photo-1576091160399-112ba8d25d1d?auto=format&fit=crop&w=1200&q=80"
                  sx={{ width: "100%", height: "100%", objectFit: "cover" }}
                  alt="Physiotherapy"
                />
              </Box>

              {/* Floating info card */}
              <motion.div
                animate={{ y: [0, -10, 0] }}
                transition={{ duration: 4, repeat: Infinity }}
                style={{
                  position: "absolute",
                  bottom: "12%",
                  left: "8%",
                }}
              >
                <Box
                  sx={{
                    bgcolor: "white",
                    p: 2,
                    borderRadius: 3,
                    boxShadow: "0 10px 30px rgba(0,0,0,0.15)",
                  }}
                >
                  <Typography fontWeight="bold">98% Recovery Rate</Typography>
                  <Typography variant="caption" color="text.secondary">
                    Trusted by thousands
                  </Typography>
                </Box>
              </motion.div>

            </motion.div>
          </Grid>

        </Grid>
      </Container>
    </Box>
  );
}
