// src/components/About.jsx
import { Container, Typography, Grid, Card, CardContent } from "@mui/material";
import { motion } from "framer-motion";

const aboutData = [
  {
    title: "Expert Care",
    desc: "We provide world-class physiotherapy treatments with experienced professionals."
  },
  {
    title: "Modern Clinics",
    desc: "State-of-the-art equipment and comfortable environments."
  },
  {
    title: "Personalized Treatment",
    desc: "Customized therapy plans for every patient."
  }
];

export default function About() {
  return (
    <Container sx={{ py: 6 }}>
      <Typography variant="h4" textAlign="center" gutterBottom>
        About PhysioTattva
      </Typography>

      <Grid container spacing={3}>
        {aboutData.map((item, index) => (
          <Grid item xs={12} md={4} key={index}>
            <motion.div
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
            >
              <Card sx={{ height: "100%" }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {item.title}
                  </Typography>
                  <Typography>
                    {item.desc}
                  </Typography>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}
