// src/components/Testimonials.jsx
import { Container, Typography, Card, CardContent, Box } from "@mui/material";
import { motion } from "framer-motion";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const testimonials = [
  { name: "Rahul", text: "Great recovery and professional care!" },
  { name: "Anjali", text: "Highly focused and effective therapists." },
  { name: "Suresh", text: "Back pain relief that truly works." },
  { name: "Priya", text: "Excellent service and friendly doctors." },
  { name: "Amit", text: "Best physiotherapy clinic I have visited." },
  { name: "Neha", text: "Quick recovery and modern equipment." },
  { name: "Vikram", text: "Very professional and caring staff." },
  { name: "Sneha", text: "My knee pain is completely gone now." },
  { name: "Rohit", text: "Highly recommended physiotherapy center." },
  { name: "Kavya", text: "Amazing experience and fast results." },
];

export default function Testimonials() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,       // 3 cards at a time (grid style)
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    responsive: [
      {
        breakpoint: 960,
        settings: {
          slidesToShow: 2,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <Box sx={{ bgcolor: "#f5f7fa", py: 8 }}>
      <Container>
        <Typography variant="h4" textAlign="center" fontWeight="bold" gutterBottom>
          What Our Patients Say
        </Typography>

        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Slider {...settings}>
            {testimonials.map((item, index) => (
              <Box key={index} sx={{ px: 2 }}>
                <Card
                  sx={{
                    height: "100%",
                    borderRadius: 3,
                    boxShadow: 3,
                  }}
                >
                  <CardContent>
                    <Typography
                      variant="body1"
                      sx={{ fontStyle: "italic", mb: 2 }}
                    >
                      "{item.text}"
                    </Typography>

                    <Typography
                      variant="subtitle2"
                      fontWeight="bold"
                      textAlign="right"
                    >
                      â€” {item.name}
                    </Typography>
                  </CardContent>
                </Card>
              </Box>
            ))}
          </Slider>
        </motion.div>
      </Container>
    </Box>
  );
}
